import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

const adminUsername = 'admin';
const adminPassword = 'admin123';

function InformationForToday({ isLoggedIn }) {
  const [weatherData, setWeatherData] = useState(null);
  const [weightBalanceData, setWeightBalanceData] = useState(null);

  const fetchWeatherData = () => {
    axios.get('http://127.0.0.1:5000/weather/123')
      .then(response => {
        setWeatherData(response.data);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error);
      });
  };

  const fetchWeightBalanceData = () => {
    axios.get('http://127.0.0.1:5000/weight_balance/123')
      .then(response => {
        setWeightBalanceData(response.data);
      })
      .catch(error => {
        console.error('Error fetching weight/balance data:', error);
      });
  };

  return (
    <div>
      {isLoggedIn && (
        <>
          <h3>Information for Today</h3>
          <div>
            {weatherData && <p>Weather: {weatherData.weather}</p>}
            {weightBalanceData && (
              <p>
                Weight/Balance: Weight - {weightBalanceData.weight}, Balance - {weightBalanceData.balance}
              </p>
            )}
          </div>
          <div>
            <button onClick={fetchWeatherData}>Weather</button>
            <button onClick={fetchWeightBalanceData}>Weight/Balance</button>
            <button>Help</button>
            <button>Contact Us</button>
          </div>
        </>
      )}
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    const isAdmin = username === adminUsername && password === adminPassword;
    if (isAdmin || users.some(user => user.username === username && user.password === password)) {
      setIsLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setPassword('');
  };

  return (
    <div className="App">
      <header className="App-header">
        {!isLoggedIn && (
          <div>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Login</button>
          </div>
        )}
        {isLoggedIn && <button onClick={handleLogout}>Logout</button>}
      </header>
      <InformationForToday isLoggedIn={isLoggedIn} />
    </div>
  );
}

export default App;
