import React, { useState } from 'react';
import './App.css';
import SecondPage from './Components/secondpage';
import logo from './logo1.jpg';

const users = [
  { username: 'user1', password: 'password1' },
  { username: 'user2', password: 'password2' },
];

const adminUsername = 'admin';
const adminPassword = 'admin123';

function TitleSlide({ toggleChat }) {
  return (
    <div className="title-slide">
      <div className="logo">
        <img src={logo} alt="Logo" />
      </div>
      <h1>Aviation Information</h1>
      <h2>Your Comprehensive Source for Flight Planning and Resources</h2>
      <div className="quick-links">
        <a href="#">Weather</a>
        <a href="#">Airports</a>
        <a href="#">Flight Tracking</a>
        <a href="#">Safety Notices</a>
        {/* Add a chat button */}
        <button onClick={toggleChat}>Chat</button>
      </div>
    </div>
  );
}

function ChatModal({ closeModal }) {
  // Add your chat interface here
  return (
    <div className="chat-modal">
      <button onClick={closeModal}>Close</button>
      {/* Chat interface */}
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showChat, setShowChat] = useState(false);

  const handleLogin = () => {
    const isAdmin = username === adminUsername && password === adminPassword;
    const foundUser = users.find(user => user.username === username && user.password === password);
    if (isAdmin || foundUser) {
      setIsLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setPassword('');
  };

  const toggleChat = () => {
    setShowChat(!showChat);
  };

  return (
    <div className="App">
      <header className="App-header">
        <TitleSlide toggleChat={toggleChat} />
        {!isLoggedIn && (
          <div>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Login</button>
          </div>
        )}
        {isLoggedIn && <button onClick={handleLogout}>Logout</button>}
        {showChat && <ChatModal closeModal={toggleChat} />}
      </header>
      {isLoggedIn && <SecondPage />}
    </div>
  );
}

export default App;
