import React, { useState } from 'react';
import './App.css';
import SecondPage from './Components/secondpage';
import logo from './logo1.jpg';
import axios from 'axios';

const users = [
  { username: 'user1', password: 'password1' },
  { username: 'user2', password: 'password2' },
];

const adminUsername = 'admin';
const adminPassword = 'admin123';

function InformationForToday() {
  const [weatherData, setWeatherData] = useState(null);
  const [weightBalanceData, setWeightBalanceData] = useState(null);

  const fetchWeatherData = () => {
    axios.get('http://127.0.0.1:5000/weather/123')
      .then(response => {
        setWeatherData(response.data);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error);
      });
  };

  const fetchWeightBalanceData = () => {
    axios.get('http://127.0.0.1:5000/weight_balance/123')
      .then(response => {
        setWeightBalanceData(response.data);
      })
      .catch(error => {
        console.error('Error fetching weight/balance data:', error);
      });
  };

  return (
    <div>
      <h3>Information for Today</h3>
      <div>
        {weatherData && <p>Weather: {weatherData.weather}</p>}
        {weightBalanceData && (
          <p>
            Weight/Balance: Weight - {weightBalanceData.weight}, Balance - {weightBalanceData.balance}
          </p>
        )}
      </div>
      <div>
        <button onClick={fetchWeatherData}>Weather</button>
        <button onClick={fetchWeightBalanceData}>Weight/Balance</button>
        <button>Help</button>
        <button>Contact Us</button>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showChat, setShowChat] = useState(false);

  const handleLogin = () => {
    const isAdmin = username === adminUsername && password === adminPassword;
    const foundUser = users.find(user => user.username === username && user.password === password);
    if (isAdmin || foundUser) {
      setIsLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setPassword('');
  };

  const toggleChat = () => {
    setShowChat(!showChat);
  };

  return (
    <div className="App">
      <header className="App-header">
        {!isLoggedIn && (
          <div>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Login</button>
          </div>
        )}
        {isLoggedIn && <button onClick={handleLogout}>Logout</button>}
        {showChat && <ChatModal closeModal={toggleChat} />}
      </header>
      {isLoggedIn && <SecondPage />}
      <InformationForToday />
    </div>
  );
}

export default App;
