import React, { useState } from 'react';
import './App.css';
import SecondPage from './Components/secondpage';
import logo from './logo1.jpg';

const users = [
  { username: 'user1', password: 'password1' },
  { username: 'user2', password: 'password2' },
];

const adminUsername = 'admin';
const adminPassword = 'admin123';

function TitleSlide() {
  return (
    <div className="title-slide">
      <div className="logo">
        <img src={logo} alt="Logo" />
      </div>
      <h1>Aviation Information</h1>
      <h2>Your Comprehensive Source for Flight Planning and Resources</h2>
      <div className="quick-links">
        <a href="#">Weather</a>
        <a href="#">Airports</a>
        <a href="#">Flight Tracking</a>
        <a href="#">Safety Notices</a>
        {/* Add a chat button */}
        <button onClick={() => window.location.href = `mailto:gblack8@uncc.edu?subject=Chat%20with%20Company`}>Chat</button>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    const isAdmin = username === adminUsername && password === adminPassword;
    const foundUser = users.find(user => user.username === username && user.password === password);
    if (isAdmin || foundUser) {
      setIsLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setPassword('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <TitleSlide />
        {!isLoggedIn && (
          <div>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Login</button>
          </div>
        )}
        {isLoggedIn && <button onClick={handleLogout}>Logout</button>}
      </header>
      {isLoggedIn && <SecondPage />}
    </div>
  );
}

export default App;
