import React, { useState } from 'react';
import './App.css';
import SecondPage from './Components/secondpage';
import logo from './logo1.jpg';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showChat, setShowChat] = useState(false);
  const [weatherData, setWeatherData] = useState(null);
  const [weightBalanceData, setWeightBalanceData] = useState(null);

  const adminUsername = 'admin';
  const adminPassword = 'admin123';

  const handleLogin = () => {
    const isAdmin = username === adminUsername && password === adminPassword;
    // Perform login validation logic here
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setPassword('');
  };

  const toggleChat = () => {
    setShowChat(!showChat);
  };

  const fetchWeatherData = () => {
    // Make GET request to fetch weather data from backend
    fetch(`http://localhost:5000/weather/123`)
      .then(response => response.json())
      .then(data => setWeatherData(data))
      .catch(error => console.error('Error fetching weather data:', error));
  };

  const fetchWeightBalanceData = () => {
    // Make GET request to fetch weight balance data from backend
    fetch(`http://localhost:5000/weight_balance/123`)
      .then(response => response.json())
      .then(data => setWeightBalanceData(data))
      .catch(error => console.error('Error fetching weight balance data:', error));
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="title-slide">
          <div className="logo">
            <img src={logo} alt="Logo" />
          </div>
          <h1>Aviation Information</h1>
          <h2>Your Comprehensive Source for Flight Planning and Resources</h2>
          <div className="quick-links">
            <button onClick={fetchWeatherData}>Weather</button>
            <button onClick={fetchWeightBalanceData}>Weight and Balance</button>
            {/* Add more buttons for other functionalities */}
            <button onClick={toggleChat}>Chat</button>
          </div>
        </div>
        {/* Add login/logout functionality */}
        {!isLoggedIn && (
          <div>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Login</button>
          </div>
        )}
        {isLoggedIn && <button onClick={handleLogout}>Logout</button>}
        {showChat && <div className="chat-modal">Chat interface</div>}
      </header>
      {/* Display weather and weight balance data */}
      {weatherData && <div>Weather: {weatherData.weather}</div>}
      {weightBalanceData && (
        <div>
          Weight: {weightBalanceData.weight}, Balance: {weightBalanceData.balance}
        </div>
      )}
      {/* Display other components based on user interaction */}
      {isLoggedIn && <SecondPage />}
    </div>
  );
}

export default App;
